import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useApp } from '../context/AppContext';
import EventCard from '../components/EventCard';
import SearchBar from '../components/SearchBar';
import { FadeIn, StaggerContainer, StaggerItem } from '../components/AnimatedComponents';
import './Home.css';

const Home = () => {
  const { events, setSearchFilters } = useApp();
  const [featuredEvents, setFeaturedEvents] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Simulate API call
    setTimeout(() => {
      setFeaturedEvents(events.slice(0, 6));
      setLoading(false);
    }, 1000);
  }, [events]);

  const handleSearch = (searchParams) => {
    // Redirect to events page with search params
    const params = new URLSearchParams();
    if (searchParams.searchTerm) params.append('search', searchParams.searchTerm);
    if (searchParams.category) params.append('category', searchParams.category);
    if (searchParams.location) params.append('location', searchParams.location);

    window.location.href = `/events?${params.toString()}`;
  };

  const categories = [...new Set(events.map(event => event.category))];

  return (
    <div className="home-page">
      {/* Hero Section */}
      <section className="hero-section">
        <div className="container">
          <div className="hero-content">
            <FadeIn delay={0.2}>
              <h1 className="hero-title">
                Discover Amazing Events
                <br />
                Near You
              </h1>
            </FadeIn>
            <FadeIn delay={0.4}>
              <p className="hero-subtitle">
                Connect with your community through local events, workshops, and experiences
              </p>
            </FadeIn>

            <FadeIn delay={0.6}>
              <div className="hero-search">
                <SearchBar 
                  onSearch={handleSearch}
                  categories={categories}
                />
              </div>
            </FadeIn>

            <FadeIn delay={0.8}>
              <div className="hero-stats">
              <div className="stat">
                <div className="stat-number">10,000+</div>
                <div className="stat-label">Events</div>
              </div>
              <div className="stat">
                <div className="stat-number">50,000+</div>
                <div className="stat-label">Community Members</div>
              </div>
              <div className="stat">
                <div className="stat-number">500+</div>
                <div className="stat-label">Cities</div>
              </div>
            </div>
            </FadeIn>
          </div>
        </div>
      </section>

      {/* Featured Events Section */}
      <section className="featured-section">
        <div className="container">
          <div className="section-header">
            <h2>Featured Events</h2>
            <p>Don't miss out on these amazing upcoming events</p>
            <Link to="/events" className="btn btn-secondary">
              View All Events
              <i className="fas fa-arrow-right"></i>
            </Link>
          </div>

          {loading ? (
            <div className="loading">
              <div className="spinner"></div>
            </div>
          ) : (
            <StaggerContainer className="events-grid grid grid-cols-3">
              {featuredEvents.map((event, index) => (
                <StaggerItem key={event.id}>
                  <EventCard event={event} />
                </StaggerItem>
              ))}
            </StaggerContainer>
          )}
        </div>
      </section>

      {/* Categories Section */}
      <section className="categories-section">
        <div className="container">
          <div className="section-header">
            <h2>Browse by Category</h2>
            <p>Find events that match your interests</p>
          </div>

          <div className="categories-grid">
            {[
              { name: 'Music', icon: 'fa-music', count: '2,500+' },
              { name: 'Sports', icon: 'fa-futbol', count: '1,800+' },
              { name: 'Technology', icon: 'fa-laptop', count: '1,200+' },
              { name: 'Food & Drink', icon: 'fa-utensils', count: '900+' },
              { name: 'Arts & Culture', icon: 'fa-palette', count: '750+' },
              { name: 'Business', icon: 'fa-briefcase', count: '650+' }
            ].map((category, index) => (
              <Link
                key={index}
                to={`/events?category=${category.name}`}
                className="category-card"
              >
                <div className="category-icon">
                  <i className={`fas ${category.icon}`}></i>
                </div>
                <h3>{category.name}</h3>
                <p>{category.count} events</p>
              </Link>
            ))}
          </div>
        </div>
      </section>

      {/* CTA Section */}
      <section className="cta-section">
        <div className="container">
          <div className="cta-content">
            <h2>Ready to Create Your Own Event?</h2>
            <p>Share your passion with the community and bring people together</p>
            <Link to="/create-event" className="btn btn-primary btn-large">
              <i className="fas fa-plus"></i>
              Create Your Event
            </Link>
          </div>
        </div>
      </section>
    </div>
  );
};

export default Home;
