import { gsap } from 'gsap';

// Animation utility functions for consistent animations across the app
export const AnimationUtils = {
  // Button animations
  initButton: (element) => {
    if (!element) return;

    // Set initial state
    gsap.set(element, {
      scale: 1,
      rotation: 0,
      transformOrigin: "center"
    });

    const handleMouseEnter = () => {
      gsap.to(element, {
        scale: 1.05,
        y: -2,
        boxShadow: "0 8px 25px rgba(0,0,0,0.15)",
        duration: 0.3,
        ease: "power2.out"
      });
    };

    const handleMouseLeave = () => {
      gsap.to(element, {
        scale: 1,
        y: 0,
        boxShadow: "0 4px 15px rgba(0,0,0,0.1)",
        duration: 0.3,
        ease: "power2.out"
      });
    };

    const handleMouseDown = () => {
      gsap.to(element, {
        scale: 0.95,
        duration: 0.1,
        ease: "power2.out"
      });
    };

    const handleMouseUp = () => {
      gsap.to(element, {
        scale: 1.05,
        duration: 0.2,
        ease: "back.out(1.7)"
      });
    };

    element.addEventListener('mouseenter', handleMouseEnter);
    element.addEventListener('mouseleave', handleMouseLeave);
    element.addEventListener('mousedown', handleMouseDown);
    element.addEventListener('mouseup', handleMouseUp);

    return () => {
      element.removeEventListener('mouseenter', handleMouseEnter);
      element.removeEventListener('mouseleave', handleMouseLeave);
      element.removeEventListener('mousedown', handleMouseDown);
      element.removeEventListener('mouseup', handleMouseUp);
    };
  },

  // Primary button with special effects
  initPrimaryButton: (element) => {
    if (!element) return;

    gsap.set(element, {
      scale: 1,
      rotation: 0,
      transformOrigin: "center"
    });

    // Pulse animation
    const pulseAnimation = gsap.to(element, {
      boxShadow: "0 0 0 10px rgba(255, 107, 107, 0)",
      duration: 1.5,
      repeat: -1,
      ease: "power2.out"
    });

    const handleMouseEnter = () => {
      pulseAnimation.pause();
      gsap.to(element, {
        scale: 1.08,
        y: -3,
        rotation: 1,
        boxShadow: "0 10px 30px rgba(255, 107, 107, 0.3)",
        duration: 0.3,
        ease: "power2.out"
      });
    };

    const handleMouseLeave = () => {
      gsap.to(element, {
        scale: 1,
        y: 0,
        rotation: 0,
        duration: 0.3,
        ease: "power2.out",
        onComplete: () => pulseAnimation.resume()
      });
    };

    const handleClick = () => {
      gsap.to(element, {
        scale: 0.92,
        duration: 0.1,
        yoyo: true,
        repeat: 1,
        ease: "power2.out"
      });
    };

    element.addEventListener('mouseenter', handleMouseEnter);
    element.addEventListener('mouseleave', handleMouseLeave);
    element.addEventListener('click', handleClick);

    return () => {
      element.removeEventListener('mouseenter', handleMouseEnter);
      element.removeEventListener('mouseleave', handleMouseLeave);
      element.removeEventListener('click', handleClick);
      pulseAnimation.kill();
    };
  },

  // Card animations
  initCard: (element) => {
    if (!element) return;

    gsap.set(element, {
      scale: 1,
      y: 0,
      transformOrigin: "center"
    });

    const handleMouseEnter = () => {
      gsap.to(element, {
        scale: 1.02,
        y: -5,
        boxShadow: "0 20px 40px rgba(0,0,0,0.15)",
        duration: 0.4,
        ease: "power2.out"
      });
    };

    const handleMouseLeave = () => {
      gsap.to(element, {
        scale: 1,
        y: 0,
        boxShadow: "0 8px 20px rgba(0,0,0,0.08)",
        duration: 0.4,
        ease: "power2.out"
      });
    };

    element.addEventListener('mouseenter', handleMouseEnter);
    element.addEventListener('mouseleave', handleMouseLeave);

    return () => {
      element.removeEventListener('mouseenter', handleMouseEnter);
      element.removeEventListener('mouseleave', handleMouseLeave);
    };
  },

  // Tab animations
  initTab: (element, isActive = false) => {
    if (!element) return;

    gsap.set(element, {
      scale: 1,
      y: 0,
      transformOrigin: "center"
    });

    const handleMouseEnter = () => {
      if (!isActive) {
        gsap.to(element, {
          scale: 1.05,
          y: -2,
          duration: 0.3,
          ease: "power2.out"
        });
      }
    };

    const handleMouseLeave = () => {
      if (!isActive) {
        gsap.to(element, {
          scale: 1,
          y: 0,
          duration: 0.3,
          ease: "power2.out"
        });
      }
    };

    const handleClick = () => {
      gsap.to(element, {
        scale: 0.95,
        duration: 0.1,
        yoyo: true,
        repeat: 1,
        ease: "power2.out"
      });
    };

    element.addEventListener('mouseenter', handleMouseEnter);
    element.addEventListener('mouseleave', handleMouseLeave);
    element.addEventListener('click', handleClick);

    return () => {
      element.removeEventListener('mouseenter', handleMouseEnter);
      element.removeEventListener('mouseleave', handleMouseLeave);
      element.removeEventListener('click', handleClick);
    };
  },

  // Entrance animations
  fadeInUp: (element, delay = 0) => {
    if (!element) return;

    gsap.fromTo(element, 
      {
        opacity: 0,
        y: 30,
        scale: 0.9
      },
      {
        opacity: 1,
        y: 0,
        scale: 1,
        duration: 0.6,
        delay,
        ease: "power2.out"
      }
    );
  },

  slideInLeft: (element, delay = 0) => {
    if (!element) return;

    gsap.fromTo(element,
      {
        opacity: 0,
        x: -50,
        scale: 0.9
      },
      {
        opacity: 1,
        x: 0,
        scale: 1,
        duration: 0.6,
        delay,
        ease: "back.out(1.7)"
      }
    );
  },

  bounceIn: (element, delay = 0) => {
    if (!element) return;

    gsap.fromTo(element,
      {
        opacity: 0,
        scale: 0
      },
      {
        opacity: 1,
        scale: 1,
        duration: 0.8,
        delay,
        ease: "back.out(1.7)"
      }
    );
  },

  // Special effects
  addRippleEffect: (element) => {
    if (!element) return;

    const handleClick = (e) => {
      const rect = element.getBoundingClientRect();
      const ripple = document.createElement('div');
      const size = Math.max(rect.width, rect.height);
      const x = e.clientX - rect.left - size / 2;
      const y = e.clientY - rect.top - size / 2;

      ripple.style.cssText = `
        position: absolute;
        width: ${size}px;
        height: ${size}px;
        left: ${x}px;
        top: ${y}px;
        background: rgba(255, 255, 255, 0.5);
        border-radius: 50%;
        pointer-events: none;
        z-index: 1000;
      `;

      element.appendChild(ripple);

      gsap.fromTo(ripple,
        {
          scale: 0,
          opacity: 1
        },
        {
          scale: 1,
          opacity: 0,
          duration: 0.6,
          ease: "power2.out",
          onComplete: () => {
            ripple.remove();
          }
        }
      );
    };

    element.addEventListener('click', handleClick);
    element.style.position = 'relative';
    element.style.overflow = 'hidden';

    return () => {
      element.removeEventListener('click', handleClick);
    };
  }
};

// Auto-initialize animations based on classes
export const initAllAnimations = () => {
  // Initialize buttons
  document.querySelectorAll('.btn').forEach((btn, index) => {
    if (btn.classList.contains('btn-primary')) {
      AnimationUtils.initPrimaryButton(btn);
      AnimationUtils.addRippleEffect(btn);
    } else {
      AnimationUtils.initButton(btn);
    }

    // Stagger entrance animations
    AnimationUtils.fadeInUp(btn, index * 0.1);
  });

  // Initialize cards
  document.querySelectorAll('.event-card, .category-card, .action-card').forEach((card, index) => {
    AnimationUtils.initCard(card);
    AnimationUtils.fadeInUp(card, index * 0.1);
  });

  // Initialize tabs
  document.querySelectorAll('.tab-btn, .auth-tab').forEach((tab, index) => {
    const isActive = tab.classList.contains('active');
    AnimationUtils.initTab(tab, isActive);
    AnimationUtils.slideInLeft(tab, index * 0.1);
  });

  // Initialize nav links
  document.querySelectorAll('.nav-link').forEach((link, index) => {
    AnimationUtils.initButton(link);
    AnimationUtils.slideInLeft(link, index * 0.1);
  });
};

export default AnimationUtils;
