import React, { useEffect, useRef } from 'react';
import { gsap } from 'gsap';
import './CursorFollower.css';

const CursorFollower = () => {
  const cursorRef = useRef(null);
  const cursorDotRef = useRef(null);
  const isHovering = useRef(false);

  useEffect(() => {
    // Don't show on mobile/touch devices
    if ('ontouchstart' in window) {
      return;
    }

    const cursor = cursorRef.current;
    const cursorDot = cursorDotRef.current;

    if (!cursor || !cursorDot) return;

    // Set initial position
    gsap.set(cursor, { xPercent: -50, yPercent: -50 });
    gsap.set(cursorDot, { xPercent: -50, yPercent: -50 });

    const updateCursor = (e) => {
      // Animate main cursor with smooth follow - optimized for dark mode
      gsap.to(cursor, {
        x: e.clientX,
        y: e.clientY,
        duration: 0.15,
        ease: "power2.out"
      });

      // Animate dot with faster follow - optimized for dark mode
      gsap.to(cursorDot, {
        x: e.clientX,
        y: e.clientY,
        duration: 0.05,
        ease: "power2.out"
      });
    };

    const handleMouseEnter = () => {
      isHovering.current = true;

      gsap.to(cursor, {
        scale: 1.5,
        backgroundColor: '#FF6B6B',
        duration: 0.3,
        ease: "power2.out"
      });

      gsap.to(cursorDot, {
        scale: 0.5,
        duration: 0.3,
        ease: "power2.out"
      });
    };

    const handleMouseLeave = () => {
      isHovering.current = false;

      gsap.to(cursor, {
        scale: 1,
        backgroundColor: 'rgba(255, 107, 107, 0.1)',
        duration: 0.3,
        ease: "power2.out"
      });

      gsap.to(cursorDot, {
        scale: 1,
        duration: 0.3,
        ease: "power2.out"
      });
    };

    const handleMouseDown = () => {
      gsap.to([cursor, cursorDot], {
        scale: 0.8,
        duration: 0.1,
        ease: "power2.out"
      });
    };

    const handleMouseUp = () => {
      gsap.to(cursor, {
        scale: isHovering.current ? 1.5 : 1,
        duration: 0.2,
        ease: "power2.out"
      });

      gsap.to(cursorDot, {
        scale: isHovering.current ? 0.5 : 1,
        duration: 0.2,
        ease: "power2.out"
      });
    };

    // Add event listeners
    document.addEventListener('mousemove', updateCursor);
    document.addEventListener('mousedown', handleMouseDown);
    document.addEventListener('mouseup', handleMouseUp);

    // Add hover listeners to interactive elements
    const interactiveSelectors = 'button, .btn, a, .event-card, .category-card, .action-card';

    const setupHoverListeners = () => {
      const elements = document.querySelectorAll(interactiveSelectors);

      elements.forEach(element => {
        element.addEventListener('mouseenter', handleMouseEnter);
        element.addEventListener('mouseleave', handleMouseLeave);
      });

      return () => {
        elements.forEach(element => {
          element.removeEventListener('mouseenter', handleMouseEnter);
          element.removeEventListener('mouseleave', handleMouseLeave);
        });
      };
    };

    // Setup initial listeners
    const cleanupHover = setupHoverListeners();

    // Re-setup listeners when DOM changes (for dynamic content)
    const observer = new MutationObserver(() => {
      cleanupHover();
      setTimeout(setupHoverListeners, 100); // Small delay to ensure DOM is ready
    });

    observer.observe(document.body, {
      childList: true,
      subtree: true
    });

    // Cleanup
    return () => {
      document.removeEventListener('mousemove', updateCursor);
      document.removeEventListener('mousedown', handleMouseDown);
      document.removeEventListener('mouseup', handleMouseUp);
      cleanupHover();
      observer.disconnect();
    };
  }, []);

  // Don't render on mobile
  if ('ontouchstart' in window) {
    return null;
  }

  return (
    <>
      <div 
        ref={cursorRef}
        className="cursor-follower"
        style={{
          position: 'fixed',
          top: 0,
          left: 0,
          width: '32px',
          height: '32px',
          backgroundColor: 'rgba(255, 107, 107, 0.1)',
          border: '2px solid #FF6B6B',
          borderRadius: '50%',
          pointerEvents: 'none',
          zIndex: 9999,
          mixBlendMode: 'difference'
        }}
      />
      <div 
        ref={cursorDotRef}
        className="cursor-dot"
        style={{
          position: 'fixed',
          top: 0,
          left: 0,
          width: '6px',
          height: '6px',
          backgroundColor: '#FF6B6B',
          borderRadius: '50%',
          pointerEvents: 'none',
          zIndex: 10000
        }}
      />
    </>
  );
};

export default CursorFollower;
