import React from 'react';
import { Link } from 'react-router-dom';
import { useApp } from '../context/AppContext';
import './EventCard.css';

const EventCard = ({ event, showActions = false, showDelete = false }) => {
  const { deleteEvent, user, isAuthenticated } = useApp();

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    const month = date.toLocaleDateString('en-US', { month: 'short' });
    const day = date.getDate();
    return { month, day };
  };

  const formatTime = (timeString) => {
    return new Date(`2000-01-01T${timeString}`).toLocaleTimeString('en-US', {
      hour: 'numeric',
      minute: '2-digit',
      hour12: true
    });
  };

  const handleDeleteEvent = (e) => {
    e.preventDefault();
    e.stopPropagation();
    if (window.confirm(`Are you sure you want to delete "${event.title}"? This action cannot be undone.`)) {
      try {
        deleteEvent(event.id);
        // Show success message
        alert('Event deleted successfully!');
      } catch (error) {
        alert('Failed to delete event. Please try again.');
      }
    }
  };

  const isOwner = isAuthenticated && user && (
    event.organizer === user.name || 
    event.organizerEmail === user.email ||
    (user.eventsCreated && user.eventsCreated.includes(event.id))
  );
  const { month, day } = formatDate(event.date);

  return (
    <div className="event-card">
      {/* Date Badge */}
      <div className="event-date-badge">
        <span className="month">{month}</span>
        <span className="day">{day}</span>
      </div>

      {/* Event Image */}
      <div className="event-image">
        <img 
          src={event.image || 'https://via.placeholder.com/400x240/FF6B6B/ffffff?text=Event+Image'} 
          alt={event.title}
          onError={(e) => {
            e.target.src = 'https://via.placeholder.com/400x240/FF6B6B/ffffff?text=Event+Image';
          }}
        />
        <div className="event-category-badge">{event.category}</div>

        {/* Action buttons for owned events */}
        {(showActions && isOwner) || (showDelete && isOwner) ? (
          <div className={`event-actions ${showDelete ? 'always-visible' : ''}`}>
            <button 
              onClick={handleDeleteEvent}
              className="action-btn delete-btn"
              title="Delete Event"
            >
              <i className="fas fa-trash"></i>
            </button>
          </div>
        ) : null}

        {/* Debug info - remove this later */}
        {showDelete && (
          <div style={{
            position: 'absolute',
            top: '5px',
            left: '5px',
            background: 'red',
            color: 'white',
            padding: '2px 4px',
            fontSize: '9px',
            zIndex: 10,
            maxWidth: '120px',
            lineHeight: '1.2'
          }}>
            Owner: {isOwner ? 'YES' : 'NO'}<br/>
            Auth: {isAuthenticated ? 'YES' : 'NO'}<br/>
            UserEmail: {user?.email || 'none'}<br/>
            EventEmail: {event.organizerEmail || 'none'}<br/>
            EventOrg: {event.organizer || 'none'}<br/>
            UserName: {user?.name || 'none'}<br/>
            EventId: {event.id}<br/>
            InCreated: {user?.eventsCreated?.includes(event.id) ? 'YES' : 'NO'}
          </div>
        )}
      </div>

      {/* Event Content */}
      <div className="event-content">
        <div className="event-meta">
          <span className="event-time">
            <i className="fas fa-clock"></i>
            {formatTime(event.time)}
          </span>
          <span className="event-price">
            {event.price === 0 ? (
              <span className="price-free">Free</span>
            ) : (
              <span className="price-paid">${event.price}</span>
            )}
          </span>
        </div>

        <h3 className="event-title">{event.title}</h3>

        <div className="event-location">
          <i className="fas fa-map-marker-alt"></i>
          <span>{event.location}</span>
        </div>

        <p className="event-description">{event.description}</p>

        <div className="event-stats">
          <div className="attendees-count">
            <i className="fas fa-users"></i>
            <span>{event.attendees} attending</span>
          </div>
        </div>

        <Link to={`/events/${event.id}`} className="event-link">
          <span>View Details</span>
          <i className="fas fa-arrow-right"></i>
        </Link>
      </div>
    </div>
  );
};

export default EventCard;
