import React, { useEffect, useState, useRef } from 'react';
import { motion } from 'framer-motion';
import './CursorFollower.css';

const CursorFollower = () => {
  const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });
  const [isHovering, setIsHovering] = useState(false);
  const [cursorType, setCursorType] = useState('default');
  const [isVisible, setIsVisible] = useState(false);
  const [particles, setParticles] = useState([]);
  const [isClicking, setIsClicking] = useState(false);
  const trailPositions = useRef([]);

  useEffect(() => {
    const updateMousePosition = (e) => {
      const newPos = { x: e.clientX, y: e.clientY };
      setMousePosition(newPos);
      setIsVisible(true);

      // Update trail positions
      trailPositions.current.unshift(newPos);
      if (trailPositions.current.length > 8) {
        trailPositions.current.pop();
      }

      // Generate particles on movement (occasionally)
      if (Math.random() < 0.3) {
        generateParticle(newPos);
      }
    };

    const handleMouseDown = () => {
      setIsClicking(true);
      // Generate burst of particles on click
      for (let i = 0; i < 8; i++) {
        setTimeout(() => {
          generateParticle(mousePosition, true);
        }, i * 50);
      }
    };

    const handleMouseUp = () => setIsClicking(false);
    const handleMouseOut = () => setIsVisible(false);

    // Add event listeners
    document.addEventListener('mousemove', updateMousePosition);
    document.addEventListener('mousedown', handleMouseDown);
    document.addEventListener('mouseup', handleMouseUp);
    document.addEventListener('mouseout', handleMouseOut);

    // Add hover listeners to different types of elements
    const setupElementListeners = () => {
      const buttonElements = document.querySelectorAll('button, .btn');
      const linkElements = document.querySelectorAll('a');
      const cardElements = document.querySelectorAll('.event-card, .category-card, .action-card');
      const inputElements = document.querySelectorAll('input, textarea, select');

      const handleElementEnter = (type) => () => {
        setIsHovering(true);
        setCursorType(type);
      };

      const handleElementLeave = () => {
        setIsHovering(false);
        setCursorType('default');
      };

      buttonElements.forEach(el => {
        el.addEventListener('mouseenter', handleElementEnter('button'));
        el.addEventListener('mouseleave', handleElementLeave);
      });

      linkElements.forEach(el => {
        el.addEventListener('mouseenter', handleElementEnter('link'));
        el.addEventListener('mouseleave', handleElementLeave);
      });

      cardElements.forEach(el => {
        el.addEventListener('mouseenter', handleElementEnter('card'));
        el.addEventListener('mouseleave', handleElementLeave);
      });

      inputElements.forEach(el => {
        el.addEventListener('mouseenter', handleElementEnter('input'));
        el.addEventListener('mouseleave', handleElementLeave);
      });

      return () => {
        [...buttonElements, ...linkElements, ...cardElements, ...inputElements].forEach(el => {
          el.removeEventListener('mouseenter', handleElementEnter);
          el.removeEventListener('mouseleave', handleElementLeave);
        });
      };
    };

    const cleanup = setupElementListeners();

    return () => {
      document.removeEventListener('mousemove', updateMousePosition);
      document.removeEventListener('mousedown', handleMouseDown);
      document.removeEventListener('mouseup', handleMouseUp);
      document.removeEventListener('mouseout', handleMouseOut);
      cleanup();
    };
  }, [mousePosition]);

  const generateParticle = (position, isBurst = false) => {
    const particle = {
      id: Date.now() + Math.random(),
      x: position.x + (Math.random() - 0.5) * 20,
      y: position.y + (Math.random() - 0.5) * 20,
      size: Math.random() * 4 + 2,
      color: isBurst ? '#ff6b6b' : ['#667eea', '#764ba2', '#ff6b6b', '#4ecdc4'][Math.floor(Math.random() * 4)],
      velocityX: (Math.random() - 0.5) * 4,
      velocityY: (Math.random() - 0.5) * 4,
      life: 1,
      decay: Math.random() * 0.02 + 0.01
    };

    setParticles(prev => [...prev.slice(-20), particle]);

    // Remove particle after animation
    setTimeout(() => {
      setParticles(prev => prev.filter(p => p.id !== particle.id));
    }, 2000);
  };

  // Don't show on mobile/touch devices
  if ('ontouchstart' in window) {
    return null;
  }

  const getCursorSize = () => {
    switch (cursorType) {
      case 'button': return 50;
      case 'link': return 35;
      case 'card': return 60;
      case 'input': return 25;
      default: return 30;
    }
  };

  const getCursorColor = () => {
    switch (cursorType) {
      case 'button': return '#ff6b6b';
      case 'link': return '#4ecdc4';
      case 'card': return '#764ba2';
      case 'input': return '#feca57';
      default: return '#667eea';
    }
  };

  return (
    <>
      {/* Main cursor */}
      <motion.div
        className={`cursor-follower cursor-${cursorType}`}
        animate={{
          x: mousePosition.x - getCursorSize() / 2,
          y: mousePosition.y - getCursorSize() / 2,
          scale: isClicking ? 0.8 : (isHovering ? 1.2 : 1),
          opacity: isVisible ? 0.7 : 0,
          width: getCursorSize(),
          height: getCursorSize(),
          backgroundColor: getCursorColor()
        }}
        transition={{
          type: "spring",
          stiffness: 600,
          damping: 30,
          mass: 0.3
        }}
      />

      {/* Trail effect */}
      {trailPositions.current.map((pos, index) => (
        <motion.div
          key={index}
          className="cursor-trail"
          initial={{ 
            x: pos.x - 6, 
            y: pos.y - 6,
            opacity: 0.5 - (index * 0.06),
            scale: 1 - (index * 0.1)
          }}
          animate={{ 
            opacity: 0,
            scale: 0.5
          }}
          transition={{ 
            duration: 0.8,
            ease: "easeOut"
          }}
        />
      ))}

      {/* Particles */}
      {particles.map(particle => (
        <motion.div
          key={particle.id}
          className="cursor-particle"
          initial={{
            x: particle.x,
            y: particle.y,
            opacity: 1,
            scale: 1
          }}
          animate={{
            x: particle.x + particle.velocityX * 50,
            y: particle.y + particle.velocityY * 50,
            opacity: 0,
            scale: 0
          }}
          transition={{
            duration: 1.5,
            ease: "easeOut"
          }}
          style={{
            backgroundColor: particle.color,
            width: particle.size,
            height: particle.size
          }}
        />
      ))}

      {/* Magnetic field effect for hoverable elements */}
      {isHovering && (
        <motion.div
          className="cursor-magnetic-field"
          animate={{
            x: mousePosition.x - 40,
            y: mousePosition.y - 40,
            scale: isClicking ? 1.5 : 1,
            opacity: 0.2
          }}
          transition={{
            type: "spring",
            stiffness: 400,
            damping: 25
          }}
        />
      )}
    </>
  );
};

export default CursorFollower;
