import React from 'react';
import { Link } from 'react-router-dom';
import './EventCard.css';

const EventCard = ({ event }) => {
  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('en-US', {
      weekday: 'short',
      year: 'numeric',
      month: 'short',
      day: 'numeric'
    });
  };

  const formatTime = (timeString) => {
    return new Date(`2000-01-01T${timeString}`).toLocaleTimeString('en-US', {
      hour: 'numeric',
      minute: '2-digit',
      hour12: true
    });
  };

  return (
    <div className="event-card card">
      <div className="event-image">
        <img 
          src={event.image || '/api/placeholder/400/200'} 
          alt={event.title}
          onError={(e) => {
            e.target.src = 'https://via.placeholder.com/400x200/667eea/ffffff?text=Event+Image';
          }}
        />
        <div className="event-category">{event.category}</div>
      </div>

      <div className="event-content">
        <div className="event-date-time">
          <div className="event-date">
            <i className="fas fa-calendar"></i>
            <span>{formatDate(event.date)}</span>
          </div>
          <div className="event-time">
            <i className="fas fa-clock"></i>
            <span>{formatTime(event.time)}</span>
          </div>
        </div>

        <h3 className="event-title">{event.title}</h3>
        <p className="event-description">{event.description}</p>

        <div className="event-location">
          <i className="fas fa-map-marker-alt"></i>
          <span>{event.location}</span>
        </div>

        <div className="event-footer">
          <div className="event-attendees">
            <i className="fas fa-users"></i>
            <span>{event.attendees} attending</span>
          </div>
          <div className="event-price">
            {event.price === 0 ? (
              <span className="free-event">Free</span>
            ) : (
              <span className="paid-event">${event.price}</span>
            )}
          </div>
        </div>

        <Link to={`/events/${event.id}`} className="btn btn-primary event-btn">
          View Details
        </Link>
      </div>
    </div>
  );
};

export default EventCard;
